import { Form, Input, Row, Col, Button, Upload, Image, Divider } from "antd";
import { UploadOutlined } from "@ant-design/icons";
import ApiRum from "../../../services/rum";
import notifica from "../../../utils/notifica";
import { getBase64 } from "../../../utils/getBase64";

const Elron = (props) => {
  const [form] = Form.useForm();
  const { lang, dataForm, setLoading, setDataForm } = props;

  const onFinish = async (values) => {
    const payload = {
      ...values,
      lang,
    };

    delete payload.cuerpoalma;

    setLoading(true);
    ApiRum.put(payload)
      .then((response) => {
        const { codigo, results } = response.data;
        if (codigo === "1") {
          notifica("success");
        } else {
          notifica("error", `service return code ${codigo}`);
        }
      })
      .catch((error) => {
        notifica("error", error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const onFinishFailed = (errorInfo) => {
    console.log("Failed:", errorInfo);
  };

  const updateImageDoc = async (fieldName) => {
    console.log("fieldName", fieldName);
    const fieldName_ = form.getFieldValue(fieldName);

    if (fieldName_ && fieldName_.file.originFileObj) {
      let base64 = await getBase64(fieldName_.file.originFileObj);
      base64 = base64.split(";base64,").pop();

      const payload = {
        lang,
        base64,
        extension: fieldName_.file.type.replace("image/", ""),
        fieldName,
        imageAnterior: dataForm[fieldName].image || "",
      };

      setLoading(true);
      ApiRum.updateImageDoc(payload)
        .then((response) => {
          const { codigo, data } = response.data;
          if (codigo === "1") {
            setDataForm(data);
            notifica("success");
          } else {
            notifica("error", `service return code ${codigo}`);
          }
        })
        .catch((error) => {
          notifica("error", error);
        })
        .finally(() => {
          setLoading(false);
        });
    }
  };

  return (
    <Form
      form={form}
      labelCol={{ span: 24 }}
      wrapperCol={{ span: 24 }}
      name="FormDoc"
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
      initialValues={{
        procesoTitle: dataForm.proceso.title,
        lacanaTitle: dataForm.lacana.title,
        lacanaContent: dataForm.lacana.content,
        lamelazaTitle: dataForm.lamelaza.title,
        lamelazaContent: dataForm.lamelaza.content,
        lafermentacionTitle: dataForm.lafermentacion.title,
        lafermentacionContent: dataForm.lafermentacion.content,
        destilacionContent: dataForm.destilacion.content,
        elanejamientoTitle: dataForm.elanejamiento.title,
        elanejamientoContent: dataForm.elanejamiento.content,
        mezclasContent: dataForm.mezclas.content,
      }}
    >
      <br />
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="procesoTitle"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input />
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <br />
          <Form.Item
            name="lacanaTitle"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input />
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="lacanaContent"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={7} />
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={(40, 40)} justify="center">
        <Col span={8}>
          <div style={{ backgroundColor: "#452F21", padding: 10 }}>
            <Image
              width={300}
              height={300}
              src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/rum/image/${dataForm.lacanaimage1.image}`}
              fallback="data:image/png;base64,"
            />
          </div>
          <Form.Item
            name={`lacanaimage1`}
            extra="(400x400) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("lacanaimage1")}
          >
            Actualizar Imagen
          </Button>
        </Col>
        <Col span={1} />
        <Col span={8}>
          <div style={{ backgroundColor: "#452F21", padding: 10 }}>
            <Image
              width={300}
              height={300}
              src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/rum/image/${dataForm.lacanaimage2.image}`}
              fallback="data:image/png;base64,"
            />
          </div>
          <Form.Item
            name={`lacanaimage2`}
            extra="(400x400) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("lacanaimage2")}
          >
            Actualizar Imagen
          </Button>
        </Col>
      </Row>
      <br />
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <br />
          <Form.Item
            name="lamelazaTitle"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input />
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="lamelazaContent"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={8} />
          </Form.Item>
        </Col>
      </Row>
      <br />
      <Row gutter={(40, 40)} justify="center">
        <Col span={22}>
          <div style={{ backgroundColor: "#452F21", padding: 10 }}>
            <Image
              width={800}
              height={320}
              src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/rum/image/${dataForm.lamelaza.image}`}
              fallback="data:image/png;base64,"
            />
          </div>
          <Form.Item
            name={`lamelaza`}
            extra="(1000x400) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("lamelaza")}
          >
            Actualizar Imagen
          </Button>
        </Col>
      </Row>
      <br />
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <br />
          <Form.Item
            name="lafermentacionTitle"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input />
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="lafermentacionContent"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={8} />
          </Form.Item>
        </Col>
      </Row>
      <br />
      <br />
      <Divider />
      <Row gutter={(40, 40)} justify="center">
        <Col span={22}>
          <div style={{ backgroundColor: "#452F21", padding: 10 }}>
            <Image
              width={550}
              height={400}
              src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/rum/image/${dataForm.destilacion.image}`}
              fallback="data:image/png;base64,"
            />
          </div>
          <Form.Item
            name={`destilacion`}
            extra="(1000x800) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("destilacion")}
          >
            Actualizar Imagen
          </Button>
        </Col>
      </Row>
      <br />
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="destilacionContent"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={10} />
          </Form.Item>
        </Col>
      </Row>
      <br />
      <Divider />
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <br />
          <Form.Item
            name="elanejamientoTitle"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input />
          </Form.Item>
        </Col>
      </Row>
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="elanejamientoContent"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={10} />
          </Form.Item>
        </Col>
      </Row>

      <Row gutter={(40, 40)} justify="center">
        <Col span={22}>
          <div style={{ backgroundColor: "#452F21", padding: 10 }}>
            <Image
              width={500}
              height={240}
              src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/rum/image/${dataForm.elanejamiento.image}`}
              fallback="data:image/png;base64,"
            />
          </div>
          <Form.Item
            name={`elanejamiento`}
            extra="(1000x400) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("elanejamiento")}
          >
            Actualizar Imagen
          </Button>
        </Col>
      </Row>
      <br />
      <Divider />
      <br />
      <Row gutter={(40, 40)} justify="center">
        <Col span={22}>
          <div style={{ backgroundColor: "#452F21", padding: 10 }}>
            <Image
              width={650}
              height={600}
              src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/rum/image/${dataForm.mezclas.image}`}
              fallback="data:image/png;base64,"
            />
          </div>
          <Form.Item
            name={`mezclas`}
            extra="(1000x850) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("mezclas")}
          >
            Actualizar Imagen
          </Button>
        </Col>
      </Row>
      <br />
      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="mezclasContent"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={10} />
          </Form.Item>
        </Col>
      </Row>
      <br />
      <Divider />
      <Button type="primary" htmlType="submit">
        Actualizar
      </Button>
    </Form>
  );
};

export default Elron;
