import { Form, Input, Row, Col, Button, Upload, Image, Divider } from "antd";
import { UploadOutlined } from "@ant-design/icons";
import ApiHistory from "../../../services/history";
import notifica from "../../../utils/notifica";
import { getBase64 } from "../../../utils/getBase64";

const History = (props) => {
  const [form] = Form.useForm();
  const { lang, dataForm, setLoading, setDataForm } = props;

  const onFinish = async (values) => {
    const payload = {
      ...values,
      lang,
    };

    delete payload.cuerpoalma;

    setLoading(true);
    ApiHistory.put(payload)
      .then((response) => {
        const { codigo, results } = response.data;
        if (codigo === "1") {
          notifica("success");
        } else {
          notifica("error", `service return code ${codigo}`);
        }
      })
      .catch((error) => {
        notifica("error", error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const onFinishFailed = (errorInfo) => {
    console.log("Failed:", errorInfo);
  };

  const updateImageDoc = async (fieldName) => {
    console.log("fieldName", fieldName);
    const fieldName_ = form.getFieldValue(fieldName);

    if (fieldName_ && fieldName_.file.originFileObj) {
      let base64 = await getBase64(fieldName_.file.originFileObj);
      base64 = base64.split(";base64,").pop();

      const payload = {
        lang,
        base64,
        extension: fieldName_.file.type.replace("image/", ""),
        fieldName,
        imageAnterior: dataForm[fieldName].image || "",
      };

      setLoading(true);
      ApiHistory.updateImageDoc(payload)
        .then((response) => {
          const { codigo, data } = response.data;
          if (codigo === "1") {
            setDataForm(data);
            notifica("success");
          } else {
            notifica("error", `service return code ${codigo}`);
          }
        })
        .catch((error) => {
          notifica("error", error);
        })
        .finally(() => {
          setLoading(false);
        });
    }
  };

  return (
    <Form
      form={form}
      labelCol={{ span: 24 }}
      wrapperCol={{ span: 24 }}
      name="FormDoc"
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
      initialValues={{
        nuestrahistoria1Title: dataForm.nuestrahistoria1.title,
        nuestrahistoria1Content: dataForm.nuestrahistoria1.content,
        nuestrahistoria2Title: dataForm.nuestrahistoria2.title,
        nuestrahistoria2Content: dataForm.nuestrahistoria2.content,
        nuestrahistoria3Title: dataForm.nuestrahistoria3.title,
        nuestrahistoria3Content: dataForm.nuestrahistoria3.content,
        nuestrahistoria4Title: dataForm.nuestrahistoria4.title,
        nuestrahistoria4Content: dataForm.nuestrahistoria4.content,
      }}
    >
      <br />

      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="nuestrahistoria1Title"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="nuestrahistoria1Content"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={8} />
          </Form.Item>
        </Col>
        <Col span={4}>
          <Image
            // width={150}
            height={100}
            src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/history/image/${dataForm.nuestrahistoria1.image}`}
            fallback="data:image/png;base64,"
          />
          <Form.Item
            name={`nuestrahistoria1`}
            extra="(600x600) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("nuestrahistoria1")}
          >
            Actualizar Imagen
          </Button>
        </Col>
      </Row>

      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="nuestrahistoria2Content"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={14} />
          </Form.Item>
        </Col>
        <Col span={4} />
      </Row>

      <br />

      <Row gutter={(40, 40)} justify="center">
        <Col span={4}>
          <Form.Item
            name={`imagemedio1`}
            extra="(614x600) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("imagemedio1")}
          >
            Actualizar Imagen 1
          </Button>
        </Col>
        <Col span={4} style={{ backgroundColor: "#452F21" }}>
          <Image
            // width={150}
            height={100}
            src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/history/image/${dataForm.imagemedio1.image}`}
            fallback="data:image/png;base64,"
          />
        </Col>
        <Col span={4}>
          <Form.Item
            name={`imagemedio2`}
            extra="(614x600) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("imagemedio2")}
          >
            Actualizar Imagen 2
          </Button>
        </Col>
        <Col span={4} style={{ backgroundColor: "#452F21" }}>
          <Image
            // width={150}
            height={100}
            src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/history/image/${dataForm.imagemedio2.image}`}
            fallback="data:image/png;base64,"
          />
        </Col>
        <Col span={4}>
          <Form.Item
            name={`imagemedio3`}
            extra="(614x600) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("imagemedio3")}
          >
            Actualizar Imagen 3
          </Button>
        </Col>
        <Col span={4} style={{ backgroundColor: "#452F21" }}>
          <Image
            // width={150}
            height={100}
            src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/history/image/${dataForm.imagemedio3.image}`}
            fallback="data:image/png;base64,"
          />
        </Col>
      </Row>

      <br />
      <br />

      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="nuestrahistoria3Content"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={9} />
          </Form.Item>
        </Col>
        <Col span={4} />
      </Row>

      <Row gutter={(40, 40)} justify="center">
        <Col span={4}>
          <Form.Item
            name={`imagemedio4`}
            extra="(614x600) Máximo 500 KB. Solo Archivos .jpg, .jpeg, .png"
          >
            <Upload accept=".jpg, .jpeg, .png" maxCount={1} listType="picture">
              <Button icon={<UploadOutlined />}>Click para adjuntar</Button>
            </Upload>
          </Form.Item>
          <Button
            type="primary"
            loading={false}
            onClick={() => updateImageDoc("imagemedio4")}
          >
            Actualizar Imagen 1
          </Button>
        </Col>
        <Col span={4} style={{ backgroundColor: "#452F21" }}>
          <Image
            // width={150}
            height={100}
            src={`${process.env.NEXT_PUBLIC_URL_API_RDV}/history/image/${dataForm.imagemedio4.image}`}
            fallback="data:image/png;base64,"
          />
        </Col>
      </Row>
      <br />
      <br />

      <Row gutter={(40, 40)} justify="center">
        <Col span={16}>
          <Form.Item
            name="nuestrahistoria4Content"
            rules={[{ required: true, message: "Requerido" }]}
          >
            <Input.TextArea rows={18} />
          </Form.Item>
        </Col>
        <Col span={4} />
      </Row>

      <Divider />

      <Button type="primary" htmlType="submit">
        Actualizar
      </Button>
    </Form>
  );
};

export default History;
